package Examples;

public class FormatSpecifiers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("%8d%8s%8.1f\n", 1234, "Java", 5.63);
		System.out.printf("%-8d%-8s%-8.1f\n\n", 1234, "Java", 5.63);
		
		System.out.printf("%14d%14s%14.1f\n", 1234, "Java", 5.63);		
		System.out.printf("%13d%13s%13.1f\n", 1234, "Java", 5.63);
		System.out.printf("%12d%12s%12.1f\n", 1234, "Java", 5.63);
		System.out.printf("%11d%11s%11.1f\n", 1234, "Java", 5.63);
		System.out.printf("%10d%10s%10.1f\n", 1234, "Java", 5.63);
		System.out.printf("%9d%9s%9.1f\n", 1234, "Java", 5.63);
		System.out.printf("%8d%8s%8.1f\n", 1234, "Java", 5.63);
		System.out.printf("%7d%7s%7.1f\n", 1234, "Java", 5.63);




	}

}
